package decision_making_statements;

public class j_05_switch_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		switch (a) {
		case 10:
			System.out.println("this is 10");
			break;

		case 20:
			System.out.println("this is 20");
			break;
		}

	}

}
